mapper.select:
select * from (select a.* from 
					(select * from message order by m_num desc) a)
	where m_sender_nick = #{m_sender_nick} and limit #{startRow}, #{endRow}

select * from (select a.* from 
					(select * from message order by m_num desc) a)
	where m_receiver_nick = #{m_receiver_nick} and limit #{startRow}, #{endRow}
